// src/components/Welcome.js
import React from 'react';
import { useNavigate } from 'react-router-dom';

const Welcome = () => {
  const navigate = useNavigate();

  return (
    <div className="flex flex-col lg:flex-row items-center justify-center min-h-screen bg-gray-200 p-6">
      {/* Imagen a la izquierda en pantallas grandes */}
      <img src="/welcome-image.png" alt="Welcome" className="w-full lg:w-1/2 max-w-md mb-8 lg:mb-0 lg:mr-8" />

      {/* Contenido de texto y botones */}
      <div className="flex flex-col items-center lg:items-start">
        <h1 className="text-4xl font-bold text-center lg:text-left text-gray-800 mb-4">
          Manage your <br /> daily tasks.
        </h1>

        <p className="text-center lg:text-left text-gray-600 text-lg mb-8 max-w-md">
          Add your daily or weekly events to the calendar and save your time.
        </p>

        <div className="flex space-x-4">
          <button
            onClick={() => navigate('/register')}
            className="px-6 py-3 bg-blue-500 text-white font-semibold rounded-full hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-300"
          >
            Get Started
          </button>

          <button
            onClick={() => navigate('/login')}
            className="flex items-center justify-center w-12 h-12 bg-black text-white rounded-full hover:bg-gray-800 focus:outline-none focus:ring-2 focus:ring-gray-400"
          >
            â†’
          </button>
        </div>
      </div>
    </div>
  );
};

export default Welcome;
